import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  MessageSquare, 
  Plus, 
  Trash2, 
  Calendar, 
  Clock,
  ChevronRight,
  User
} from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';

const SessionSidebar = ({ 
  sessions = [], 
  currentSession, 
  onSelectSession, 
  onDeleteSession, 
  onNewChat,
  loading = false,
  isOpen = true 
}) => {
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  const handleDeleteClick = (sessionId, e) => {
    e.stopPropagation();
    setDeleteConfirm(sessionId);
  };

  const confirmDelete = async (sessionId) => {
    try {
      await onDeleteSession(sessionId);
      setDeleteConfirm(null);
    } catch (error) {
      console.error('Failed to delete session:', error);
      // Keep confirmation open on error
    }
  };

  const cancelDelete = (e) => {
    e.stopPropagation();
    setDeleteConfirm(null);
  };

  if (!isOpen) return null;

  return (
    <motion.div 
      initial={{ width: 0, opacity: 0 }}
      animate={{ width: 320, opacity: 1 }}
      exit={{ width: 0, opacity: 0 }}
      className="bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700 flex flex-col shadow-sm"
    >
      {/* Header */}
      <div className="p-4 border-b border-gray-200 dark:border-gray-700">
        <button
          onClick={onNewChat}
          disabled={loading}
          className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white px-4 py-3 rounded-lg transition-colors flex items-center justify-center gap-2 font-medium"
        >
          <Plus size={20} />
          {loading ? 'Creating...' : 'New Chat'}
        </button>
      </div>

      {/* Recent Conversations Header */}
      <div className="px-4 py-3 border-b border-gray-100 dark:border-gray-700">
        <h3 className="text-sm font-semibold text-gray-900 dark:text-gray-100 flex items-center gap-2">
          <MessageSquare size={16} />
          Recent Conversations
        </h3>
      </div>

      {/* Sessions List */}
      <div className="flex-1 overflow-y-auto">
        {sessions.length === 0 ? (
          <div className="p-6 text-center">
            <MessageSquare size={48} className="mx-auto text-gray-300 dark:text-gray-600 mb-3" />
            <p className="text-sm text-gray-500 dark:text-gray-400 mb-2">No conversations yet</p>
            <p className="text-xs text-gray-400 dark:text-gray-500">Click "New Chat" to start your first conversation</p>
          </div>
        ) : (
          <div className="p-2 space-y-1">
            {sessions
              .sort((a, b) => new Date(b.updated_at) - new Date(a.updated_at))
              .map((session) => {
                const isActive = currentSession?.session_id === session.session_id;
                const isDeleting = deleteConfirm === session.session_id;

                return (
                  <motion.div
                    key={session.session_id}
                    layout
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    className={`
                      group relative p-3 rounded-lg cursor-pointer transition-all duration-200 border
                      ${isActive 
                        ? 'bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800 shadow-sm' 
                        : 'bg-gray-50 dark:bg-gray-700/50 border-gray-200 dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700 hover:border-gray-300 dark:hover:border-gray-500'
                      }
                    `}
                    onClick={() => !isDeleting && onSelectSession(session.session_id)}
                  >
                    {isDeleting ? (
                      /* Delete Confirmation */
                      <div className="space-y-3">
                        <p className="text-sm font-medium text-red-600 dark:text-red-400">
                          Delete this conversation?
                        </p>
                        <div className="flex gap-2">
                          <button
                            onClick={() => confirmDelete(session.session_id)}
                            className="flex-1 bg-red-600 hover:bg-red-700 text-white text-xs py-2 px-3 rounded transition-colors"
                          >
                            Delete
                          </button>
                          <button
                            onClick={cancelDelete}
                            className="flex-1 bg-gray-300 hover:bg-gray-400 text-gray-700 text-xs py-2 px-3 rounded transition-colors"
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    ) : (
                      /* Normal Session Display */
                      <>
                        <div className="flex items-start justify-between">
                          <div className="flex-1 min-w-0">
                            {/* Session Title */}
                            <h4 className={`text-sm font-medium truncate ${
                              isActive 
                                ? 'text-blue-900 dark:text-blue-100' 
                                : 'text-gray-900 dark:text-gray-100'
                            }`}>
                              {session.title}
                            </h4>

                            {/* Last Message Preview */}
                            {session.last_message_preview && (
                              <p className="text-xs text-gray-600 dark:text-gray-400 truncate mt-1 leading-relaxed">
                                {session.last_message_preview}
                              </p>
                            )}
                          </div>

                          {/* Delete Button */}
                          <button
                            onClick={(e) => handleDeleteClick(session.session_id, e)}
                            className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-red-500 dark:text-gray-500 dark:hover:text-red-400 p-1 rounded transition-all"
                            title="Delete conversation"
                          >
                            <Trash2 size={14} />
                          </button>
                        </div>

                        {/* Session Metadata */}
                        <div className="flex items-center justify-between mt-3 pt-2 border-t border-gray-200 dark:border-gray-600">
                          <div className="flex items-center gap-3 text-xs text-gray-500 dark:text-gray-400">
                            <span className="flex items-center gap-1">
                              <MessageSquare size={12} />
                              {session.message_count}
                            </span>
                            <span className="flex items-center gap-1">
                              <Clock size={12} />
                              {formatDistanceToNow(new Date(session.updated_at), { addSuffix: true })}
                            </span>
                          </div>

                          {/* Status Badge */}
                          <span className={`
                            text-xs px-2 py-1 rounded-full font-medium
                            ${session.status === 'active' 
                              ? 'bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-400' 
                              : 'bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-400'
                            }
                          `}>
                            {session.status}
                          </span>
                        </div>

                        {/* Active Session Indicator */}
                        {isActive && (
                          <motion.div
                            layoutId="activeSession"
                            className="absolute right-2 top-1/2 transform -translate-y-1/2"
                          >
                            <ChevronRight size={16} className="text-blue-600 dark:text-blue-400" />
                          </motion.div>
                        )}
                      </>
                    )}
                  </motion.div>
                );
              })}
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="p-3 border-t border-gray-200 dark:border-gray-700">
        <div className="flex items-center gap-2 text-xs text-gray-500 dark:text-gray-400">
          <User size={12} />
          <span>default_user</span>
          <span>â€¢</span>
          <span>{sessions.length} conversation{sessions.length !== 1 ? 's' : ''}</span>
        </div>
      </div>
    </motion.div>
  );
};

export default SessionSidebar;
