import React, { useState, useEffect } from 'react';
import { Search, Plus, ExternalLink, BarChart3, Filter, Calendar, User, AlertCircle } from 'lucide-react';

const JiraPage = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(false);

  // Filters
  const [filters, setFilters] = useState({
    project: 'ALL',
    status: 'ALL',
    assignee: 'ALL',
    dateRange: '7d'
  });

  const [searchQuery, setSearchQuery] = useState('');
  const [newTicket, setNewTicket] = useState({
    project_key: '',
    summary: '',
    description: '',
    issue_type: 'Task'
  });

  useEffect(() => {
    loadProjects();
    if (activeTab === 'dashboard') {
      loadDashboard();
    }
  }, [activeTab]);

  const loadProjects = async () => {
    try {
      const response = await fetch('/api/jira/projects');
      if (response.ok) {
        const data = await response.json();
        setProjects(data.projects);
      }
    } catch (error) {
      console.error('Failed to load projects:', error);
    }
  };

  const loadDashboard = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/jira/dashboard');
      if (response.ok) {
        const data = await response.json();
        setDashboardData(data);
      }
    } catch (error) {
      console.error('Failed to load dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const searchTickets = async () => {
    if (!searchQuery.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`/api/jira/search?query=${encodeURIComponent(searchQuery)}&max_results=20`);
      if (response.ok) {
        const data = await response.json();
        setTickets(data.tickets);
      }
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const createTicket = async () => {
    if (!newTicket.project_key || !newTicket.summary) return;

    setLoading(true);
    try {
      const response = await fetch('/api/jira/ticket', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newTicket)
      });

      if (response.ok) {
        const result = await response.json();
        alert(`Ticket created: ${result.key}`);
        setNewTicket({ project_key: '', summary: '', description: '', issue_type: 'Task' });
      }
    } catch (error) {
      console.error('Create failed:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <ExternalLink className="w-8 h-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">JIRA Integration</h1>
                <p className="text-sm text-gray-500">Manage tickets and view analytics</p>
              </div>
            </div>

            {/* Tabs */}
            <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg">
              <button
                onClick={() => setActiveTab('dashboard')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === 'dashboard' 
                    ? 'bg-white text-blue-600 shadow-sm' 
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <BarChart3 size={16} className="inline mr-2" />
                Dashboard
              </button>
              <button
                onClick={() => setActiveTab('search')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === 'search' 
                    ? 'bg-white text-blue-600 shadow-sm' 
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <Search size={16} className="inline mr-2" />
                Search
              </button>
              <button
                onClick={() => setActiveTab('create')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === 'create' 
                    ? 'bg-white text-blue-600 shadow-sm' 
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <Plus size={16} className="inline mr-2" />
                Create
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">

        {/* Dashboard Tab */}
        {activeTab === 'dashboard' && (
          <div className="space-y-6">
            {/* Filters */}
            <div className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center space-x-4">
                <Filter size={16} className="text-gray-500" />
                <select 
                  value={filters.project} 
                  onChange={(e) => setFilters({...filters, project: e.target.value})}
                  className="px-3 py-1 border rounded text-sm"
                >
                  <option value="ALL">All Projects</option>
                  <option value="MBSL3">MBSL3</option>
                  <option value="UNO">UNO</option>
                </select>
                <select 
                  value={filters.dateRange} 
                  onChange={(e) => setFilters({...filters, dateRange: e.target.value})}
                  className="px-3 py-1 border rounded text-sm"
                >
                  <option value="7d">Last 7 days</option>
                  <option value="30d">Last 30 days</option>
                  <option value="90d">Last 90 days</option>
                </select>
                <button 
                  onClick={loadDashboard}
                  className="px-4 py-1 bg-blue-600 text-white rounded text-sm hover:bg-blue-700"
                >
                  Refresh
                </button>
              </div>
            </div>

            {loading ? (
              <div className="text-center py-12">Loading dashboard...</div>
            ) : dashboardData?.error ? (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <div className="flex items-center">
                  <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
                  <span className="text-red-700">{dashboardData.error}</span>
                </div>
              </div>
            ) : dashboardData ? (
              <>
                {/* Summary Cards */}
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                  <div className="bg-white p-6 rounded-lg shadow-sm border-l-4 border-blue-500">
                    <div className="flex items-center">
                      <div className="flex-1">
                        <p className="text-sm font-medium text-gray-600">MBSL3 Open</p>
                        <p className="text-3xl font-bold text-blue-600">{dashboardData.summary?.mbsl3_open || 0}</p>
                      </div>
                      <BarChart3 className="w-8 h-8 text-blue-500" />
                    </div>
                  </div>

                  <div className="bg-white p-6 rounded-lg shadow-sm border-l-4 border-green-500">
                    <div className="flex items-center">
                      <div className="flex-1">
                        <p className="text-sm font-medium text-gray-600">UNO Open</p>
                        <p className="text-3xl font-bold text-green-600">{dashboardData.summary?.uno_open || 0}</p>
                      </div>
                      <BarChart3 className="w-8 h-8 text-green-500" />
                    </div>
                  </div>

                  <div className="bg-white p-6 rounded-lg shadow-sm border-l-4 border-orange-500">
                    <div className="flex items-center">
                      <div className="flex-1">
                        <p className="text-sm font-medium text-gray-600">Recent Updates</p>
                        <p className="text-3xl font-bold text-orange-600">{dashboardData.summary?.recent_updates || 0}</p>
                      </div>
                      <Calendar className="w-8 h-8 text-orange-500" />
                    </div>
                  </div>

                  <div className="bg-white p-6 rounded-lg shadow-sm border-l-4 border-purple-500">
                    <div className="flex items-center">
                      <div className="flex-1">
                        <p className="text-sm font-medium text-gray-600">Total Open</p>
                        <p className="text-3xl font-bold text-purple-600">
                          {(dashboardData.summary?.mbsl3_open || 0) + (dashboardData.summary?.uno_open || 0)}
                        </p>
                      </div>
                      <AlertCircle className="w-8 h-8 text-purple-500" />
                    </div>
                  </div>
                </div>

                {/* Charts Row */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  {/* Status Breakdown */}
                  {dashboardData.mbsl3_status && Object.keys(dashboardData.mbsl3_status).length > 0 && (
                    <div className="bg-white p-6 rounded-lg shadow-sm">
                      <h3 className="text-lg font-semibold mb-4">MBSL3 Status Distribution</h3>
                      <div className="space-y-3">
                        {Object.entries(dashboardData.mbsl3_status).map(([status, count]) => (
                          <div key={status} className="flex items-center justify-between">
                            <div className="flex items-center">
                              <div className="w-3 h-3 bg-blue-500 rounded-full mr-3"></div>
                              <span className="text-sm font-medium">{status}</span>
                            </div>
                            <div className="flex items-center">
                              <div className="w-24 bg-gray-200 rounded-full h-2 mr-3">
                                <div 
                                  className="bg-blue-500 h-2 rounded-full" 
                                  style={{width: `${(count / Math.max(...Object.values(dashboardData.mbsl3_status))) * 100}%`}}
                                ></div>
                              </div>
                              <span className="text-sm font-bold text-gray-700">{count}</span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Recent Activity */}
                  {dashboardData.recent_tickets && dashboardData.recent_tickets.length > 0 && (
                    <div className="bg-white p-6 rounded-lg shadow-sm">
                      <h3 className="text-lg font-semibold mb-4">Recent Activity</h3>
                      <div className="space-y-3 max-h-80 overflow-y-auto">
                        {dashboardData.recent_tickets.map((ticket) => (
                          <div key={ticket.key} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
                            <div className="flex-1 min-w-0">
                              <div className="flex items-center space-x-2">
                                <span className="font-medium text-blue-600">{ticket.key}</span>
                                <span className={`px-2 py-1 text-xs rounded-full ${
                                  ticket.status === 'Done' ? 'bg-green-100 text-green-800' :
                                  ticket.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {ticket.status}
                                </span>
                              </div>
                              <p className="text-sm text-gray-600 truncate mt-1">{ticket.summary}</p>
                              <p className="text-xs text-gray-500 mt-1">
                                <User size={12} className="inline mr-1" />
                                {ticket.assignee}
                              </p>
                            </div>
                            <a 
                              href={ticket.url} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="ml-3 text-gray-400 hover:text-gray-600"
                            >
                              <ExternalLink size={16} />
                            </a>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="text-center py-12 text-gray-500">No dashboard data available</div>
            )}
          </div>
        )}

        {/* Search Tab */}
        {activeTab === 'search' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex gap-4 mb-6">
              <input
                type="text"
                placeholder="Search tickets (e.g., MBSL3, UNO, MBSL3-1234)..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && searchTickets()}
                className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <button
                onClick={searchTickets}
                disabled={loading}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 flex items-center"
              >
                <Search size={16} className="mr-2" />
                Search
              </button>
            </div>

            {loading ? (
              <div className="text-center py-8">Searching...</div>
            ) : (
              <div className="space-y-3">
                {tickets.map((ticket) => (
                  <div key={ticket.key} className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <span className="font-medium text-blue-600">{ticket.key}</span>
                        <span className={`px-2 py-1 text-xs rounded-full ${
                          ticket.status === 'Done' ? 'bg-green-100 text-green-800' :
                          ticket.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {ticket.status}
                        </span>
                      </div>
                      <a
                        href={ticket.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-gray-500 hover:text-gray-700"
                      >
                        <ExternalLink size={16} />
                      </a>
                    </div>
                    <p className="text-gray-800 mt-2">{ticket.summary}</p>
                    <div className="flex items-center gap-4 mt-3 text-sm text-gray-500">
                      <span>Assignee: {ticket.assignee}</span>
                      <span>Updated: {new Date(ticket.updated).toLocaleDateString()}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Create Tab */}
        {activeTab === 'create' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold mb-6">Create New Ticket</h2>
            <div className="space-y-4 max-w-2xl">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Project</label>
                <select
                  value={newTicket.project_key}
                  onChange={(e) => setNewTicket({...newTicket, project_key: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="">Select Project</option>
                  {projects.map((project) => (
                    <option key={project.key} value={project.key}>
                      {project.name} ({project.key})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Summary</label>
                <input
                  type="text"
                  value={newTicket.summary}
                  onChange={(e) => setNewTicket({...newTicket, summary: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Brief description of the issue"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
                <textarea
                  value={newTicket.description}
                  onChange={(e) => setNewTicket({...newTicket, description: e.target.value})}
                  rows={4}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Detailed description of the issue"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Issue Type</label>
                <select
                  value={newTicket.issue_type}
                  onChange={(e) => setNewTicket({...newTicket, issue_type: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="Task">Task</option>
                  <option value="Bug">Bug</option>
                  <option value="Story">Story</option>
                </select>
              </div>

              <button
                onClick={createTicket}
                disabled={loading || !newTicket.project_key || !newTicket.summary}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Creating...' : 'Create Ticket'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default JiraPage;
