import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || '';

const api = axios.create({
  baseURL: `${API_BASE_URL}/api`,
  timeout: 120000,  // 2 minutes for chat
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor for logging
api.interceptors.request.use(
  (config) => {
    console.log(`API Request: ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Response Error:', error);

    if (error.response?.status === 500) {
      console.error('Server Error:', error.response.data);
    } else if (error.code === 'ECONNABORTED') {
      console.error('Request timeout');
    } else if (!error.response) {
      console.error('Network error - server might be down');
    }

    return Promise.reject(error);
  }
);

export const chatAPI = {
  // Send a message and get response
  sendMessage: async (message, sessionId = null, stream = false, useInternetSearch = false, aiParams = {}) => {
    try {
      const requestBody = {
        message,
        session_id: sessionId,
        stream,
        internet_search: useInternetSearch,
        // Include AI parameters if provided
        ...(aiParams.temperature !== undefined && { temperature: aiParams.temperature }),
        ...(aiParams.maxTokens !== undefined && { max_tokens: aiParams.maxTokens }),
        ...(aiParams.topP !== undefined && { top_p: aiParams.topP }),
        ...(aiParams.model !== undefined && { model: aiParams.model }),
        ...(aiParams.customSystemPrompt !== undefined && { custom_system_prompt: aiParams.customSystemPrompt }),
        ...(aiParams.useCustomPrompt !== undefined && { use_custom_prompt: aiParams.useCustomPrompt }),
      };

      if (stream) {
        // Return a stream for server-sent events
        const response = await fetch(`/api/chat`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestBody),
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        return response;
      } else {
        const response = await api.post('/chat', requestBody);
        return response.data;
      }
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to send message');
    }
  },

  // Process streaming response
  processStream: async function* (response, onChunk = null, onComplete = null, onError = null) {
    try {
      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = '';

      while (true) {
        const { done, value } = await reader.read();

        if (done) break;

        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\n');
        buffer = lines.pop(); // Keep the incomplete line in buffer

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            try {
              const data = JSON.parse(line.slice(6));

              if (data.type === 'chunk' && data.content) {
                if (onChunk) onChunk(data.content);
                yield data.content;
              } else if (data.type === 'complete') {
                if (onComplete) onComplete(data);
                return data;
              } else if (data.type === 'error') {
                if (onError) onError(data.content);
                throw new Error(data.content);
              }
            } catch (parseError) {
              console.warn('Failed to parse streaming data:', line);
            }
          }
        }
      }
    } catch (error) {
      if (onError) onError(error.message);
      throw error;
    }
  },

  // Get conversation history
  getConversationHistory: async (sessionId) => {
    try {
      const response = await api.get(`/conversations/${sessionId}`);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to get conversation history');
    }
  },

  // Get all conversations
  getAllConversations: async () => {
    try {
      const response = await api.get('/conversations');
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to get conversations');
    }
  },

  // Delete a conversation
  deleteConversation: async (sessionId) => {
    try {
      const response = await api.delete(`/conversations/${sessionId}`);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to delete conversation');
    }
  },

  // Upload a file
  uploadFile: async (file, onProgress = null) => {
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await api.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          if (onProgress && progressEvent.total) {
            const percentCompleted = Math.round(
              (progressEvent.loaded * 100) / progressEvent.total
            );
            onProgress(percentCompleted);
          }
        },
      });

      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to upload file');
    }
  },

  // Upload document for chat
  uploadChatDocument: async (sessionId, formData) => {
    try {
      const response = await api.post(`/chat/upload?session_id=${sessionId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.detail || 'Failed to upload chat document');
    }
  },

  // Get chat documents
  getChatDocuments: async (sessionId) => {
    try {
      const response = await api.get(`/chat/documents/${sessionId}`);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.detail || 'Failed to get chat documents');
    }
  },

  // Remove chat document
  removeChatDocument: async (sessionId, docId) => {
    try {
      const response = await api.delete(`/chat/documents/${sessionId}/${docId}`);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.detail || 'Failed to remove chat document');
    }
  },

  // Get system health
  getHealth: async () => {
    try {
      const response = await api.get('/health');
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to get system health');
    }
  },

  // Get processing status
  getProcessingStatus: async (processingId) => {
    try {
      const response = await api.get(`/processing/${processingId}`);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.error || 'Failed to get processing status');
    }
  },
};

export const systemAPI = {
  // Check if backend is available
  checkBackendStatus: async () => {
    try {
      const response = await api.get('/health');
      return { 
        available: true, 
        data: response.data 
      };
    } catch (error) {
      return { 
        available: false, 
        error: error.message 
      };
    }
  },

  // Get system statistics (if available)
  getStats: async () => {
    try {
      const response = await api.get('/stats');
      return response.data;
    } catch (error) {
      // Stats endpoint might not be available
      console.warn('Stats endpoint not available:', error.message);
      return null;
    }
  },
};

export default api;
