import React from 'react';

const ComvivaLogo = ({ 
  className = "w-8 h-8", 
  variant = "icon", 
  theme = "auto",
  showTagline = false 
}) => {
  const baseClasses = "object-contain transition-opacity duration-200";

  // Full logo with text
  if (variant === "full") {
    if (theme === "dark") {
      return (
        <img 
          src="/assets/logos/comviva-logo-dark.svg"
          alt="Comviva - A Tech Mahindra Company"
          className={`${baseClasses} ${className}`}
          loading="lazy"
        />
      );
    } else if (theme === "light") {
      return (
        <img 
          src="/assets/logos/comviva-logo-light.svg"
          alt="Comviva - A Tech Mahindra Company"
          className={`${baseClasses} ${className}`}
          loading="lazy"
        />
      );
    } else {
      // Auto mode - responsive to system theme
      return (
        <div className={`relative ${className}`}>
          <img 
            src="/assets/logos/comviva-logo-light.svg"
            alt="Comviva - A Tech Mahindra Company"
            className={`${baseClasses} dark:hidden w-full h-full`}
            loading="lazy"
          />
          <img 
            src="/assets/logos/comviva-logo-dark.svg"
            alt="Comviva - A Tech Mahindra Company"
            className={`${baseClasses} hidden dark:block w-full h-full`}
            loading="lazy"
          />
        </div>
      );
    }
  }

  // Icon only variant
  return (
    <img 
      src="/assets/logos/comviva-icon.svg"
      alt="Comviva"
      className={`${baseClasses} ${className}`}
      loading="lazy"
    />
  );
};

// Inline SVG component for cases where external files aren't available
const ComvivaIcon = ({ className = "w-8 h-8" }) => (
  <svg 
    className={className}
    viewBox="0 0 40 40" 
    fill="none" 
    xmlns="http://www.w3.org/2000/svg"
    role="img"
    aria-label="Comviva"
  >
    <defs>
      <linearGradient id="comvivaInlineGradient" x1="0%" y1="0%" x2="100%" y2="100%">
        <stop offset="0%" stopColor="#E31E24" />
        <stop offset="50%" stopColor="#F47920" />
        <stop offset="100%" stopColor="#E31E24" />
      </linearGradient>
    </defs>

    <circle 
      cx="20" 
      cy="20" 
      r="18" 
      fill="url(#comvivaInlineGradient)"
      stroke="rgba(255,255,255,0.2)" 
      strokeWidth="1"
    />

    <circle cx="20" cy="20" r="8" fill="white" />

    <path 
      d="M12 8 L28 14 L20 20 L12 26 L4 20 Z" 
      fill="white"
      fillOpacity="0.9"
    />
  </svg>
);

export default ComvivaLogo;
export { ComvivaIcon };
