import React, { useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Sun, Moon } from 'lucide-react';
import useThemeStore from '../../stores/themeStore';

const SettingsDropdown = ({ isOpen, onClose, buttonRef }) => {
  const dropdownRef = useRef(null);
  const { isDark, toggleTheme } = useThemeStore();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target) &&
          buttonRef.current && !buttonRef.current.contains(event.target)) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, onClose, buttonRef]);

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          ref={dropdownRef}
          initial={{ opacity: 0, scale: 0.95, y: -10 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.95, y: -10 }}
          className="settings-dropdown w-72 bg-white dark:bg-dark-surface rounded-lg shadow-xl border border-gray-200 dark:border-dark-border z-50"
        >
          {/* Header */}
          <div className="p-4 border-b border-gray-200 dark:border-dark-border">
            <h3 className="text-sm font-semibold text-gray-900 dark:text-dark-text">Settings</h3>
          </div>

          {/* Content */}
          <div className="p-4 space-y-4">
            {/* Theme Settings */}
            <div>
              <h4 className="text-xs font-medium text-gray-700 dark:text-gray-300 mb-2">Appearance</h4>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600 dark:text-dark-muted">Theme</span>
                <button
                  onClick={toggleTheme}
                  className={`
                    relative inline-flex h-5 w-9 items-center rounded-full transition-colors duration-200 
                    ${isDark ? 'bg-comviva-primary' : 'bg-gray-300'}
                  `}
                >
                  <motion.div
                    className="inline-block h-3 w-3 rounded-full bg-white shadow-sm"
                    animate={{ x: isDark ? 20 : 4 }}
                    transition={{ type: "spring", stiffness: 500, damping: 30 }}
                  >
                    <div className="flex items-center justify-center h-full w-full">
                      {isDark ? (
                        <Moon className="h-2 w-2 text-comviva-primary" />
                      ) : (
                        <Sun className="h-2 w-2 text-amber-500" />
                      )}
                    </div>
                  </motion.div>
                </button>
              </div>
            </div>

            {/* Zoom Settings */}
            <div>
              <h4 className="text-xs font-medium text-gray-700 dark:text-gray-300 mb-2">Display Scale</h4>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-dark-muted">Zoom</span>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => setZoomLevel(Math.max(0.8, zoomLevel - 0.1))}
                      disabled={zoomLevel <= 0.8}
                      className="p-1 text-gray-400 hover:text-gray-600 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <ZoomOut className="w-3 h-3" />
                    </button>
                    <span className="text-xs font-medium text-gray-900 dark:text-dark-text min-w-[2.5rem] text-center">
                      {Math.round(zoomLevel * 100)}%
                    </span>
                    <button
                      onClick={() => setZoomLevel(Math.min(1.3, zoomLevel + 0.1))}
                      disabled={zoomLevel >= 1.3}
                      className="p-1 text-gray-400 hover:text-gray-600 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <ZoomIn className="w-3 h-3" />
                    </button>
                  </div>
                </div>

                {/* Zoom Presets */}
                <div className="grid grid-cols-3 gap-1">
                  {zoomOptions.map((option) => (
                    <button
                      key={option.value}
                      onClick={() => setZoomLevel(option.value)}
                      className={`
                        px-2 py-1 text-xs rounded transition-colors
                        ${zoomLevel === option.value
                          ? 'bg-comviva-primary text-white'
                          : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600'
                        }
                      `}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>

                <button
                  onClick={resetZoom}
                  className="flex items-center space-x-1 text-xs text-comviva-primary hover:text-blue-700 transition-colors"
                >
                  <RotateCcw className="w-3 h-3" />
                  <span>Reset</span>
                </button>
              </div>
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default SettingsDropdown;
