import React from 'react';
import { motion } from 'framer-motion';
import { User, Bot, Copy, ExternalLink, AlertCircle } from 'lucide-react';
import { format } from 'date-fns';
import ReactMarkdown from 'react-markdown';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { tomorrow } from 'react-syntax-highlighter/dist/esm/styles/prism';
import ChartRenderer from '../Charts/ChartRenderer';
import UNODashboard from '../Charts/UNODashboard';

const MessageBubble = ({ message, isLast }) => {
  const [copied, setCopied] = React.useState(false);

  const handleCopy = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy text:', err);
    }
  };

  const formatTimestamp = (timestamp) => {
    try {
      return format(new Date(timestamp), 'MMM d, h:mm a');
    } catch {
      return '';
    }
  };

  const messageVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  };

  if (message.type === 'user') {
    return (
      <motion.div
        variants={messageVariants}
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.3 }}
        className="flex justify-end items-start space-x-3"
      >
        <div className="max-w-3xl">
          <div className="user-message">
            <div className="whitespace-pre-wrap break-words">
              {message.content}
            </div>
          </div>
          <div className="text-xs text-gray-500 dark:text-gray-400 text-right mt-1">
            {formatTimestamp(message.timestamp)}
          </div>
        </div>
        <div className="w-8 h-8 bg-comviva-primary rounded-full flex items-center justify-center flex-shrink-0">
          <User className="w-4 h-4 text-white" />
        </div>
      </motion.div>
    );
  }

  if (message.type === 'error') {
    return (
      <motion.div
        variants={messageVariants}
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.3 }}
        className="flex items-start space-x-3"
      >
        <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center flex-shrink-0">
          <AlertCircle className="w-4 h-4 text-white" />
        </div>
        <div className="max-w-3xl">
          <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-2xl rounded-tl-md px-4 py-3">
            <div className="text-red-800 dark:text-red-200 whitespace-pre-wrap break-words">
              {message.content}
            </div>
          </div>
          <div className="text-xs text-gray-500 dark:text-gray-400 mt-1">
            {formatTimestamp(message.timestamp)}
          </div>
        </div>
      </motion.div>
    );
  }

  // Bot message
  return (
    <motion.div
      variants={messageVariants}
      initial="hidden"
      animate="visible"
      transition={{ duration: 0.3 }}
      className="flex items-start space-x-3"
    >
      <div className="w-8 h-8 bg-comviva-secondary rounded-full flex items-center justify-center flex-shrink-0">
        <Bot className="w-4 h-4 text-white" />
      </div>
      <div className="max-w-3xl flex-1">
        <div className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-2xl rounded-tl-md px-4 py-3 mr-auto">
          <div className="markdown-content">
            {/* Check for chart/dashboard commands */}
            {message.content.includes('[CHART:') ? (
              <div>
                {message.content.split(/\[CHART:[^\]]+\]/).map((text, index, array) => {
                  const chartMatch = message.content.match(/\[CHART:([^\]]+)\]/g);
                  return (
                    <React.Fragment key={index}>
                      <ReactMarkdown>{text}</ReactMarkdown>
                      {index < array.length - 1 && chartMatch && chartMatch[index] && (
                        <div className="my-4">
                          {chartMatch[index].includes('dashboard') && (
                            <UNODashboard dashboardType={chartMatch[index].match(/dashboard:(\w+)/)?.[1] || 'overview'} />
                          )}
                          {chartMatch[index].includes('chart') && (
                            <ChartRenderer 
                              type="bar" 
                              data={[
                                { name: 'SMS', value: 45000 },
                                { name: 'MMS', value: 12000 },
                                { name: 'RCS', value: 8000 }
                              ]}
                              title="Sample UNO Data"
                            />
                          )}
                        </div>
                      )}
                    </React.Fragment>
                  );
                })}
              </div>
            ) : (
              <ReactMarkdown
                components={{
                  code({ node, inline, className, children, ...props }) {
                    const match = /language-(\w+)/.exec(className || '');
                    return !inline && match ? (
                      <SyntaxHighlighter
                        style={tomorrow}
                        language={match[1]}
                        PreTag="div"
                        className="rounded-lg !mt-2 !mb-2"
                        {...props}
                      >
                        {String(children).replace(/\n$/, '')}
                      </SyntaxHighlighter>
                    ) : (
                      <code className={className} {...props}>
                        {children}
                      </code>
                    );
                  },
                  a({ children, href, ...props }) {
                    return (
                      <a
                        href={href}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-comviva-primary hover:text-blue-700 underline inline-flex items-center gap-1"
                        {...props}
                      >
                        {children}
                        <ExternalLink className="w-3 h-3" />
                      </a>
                    );
                  },
                }}
              >
                {message.content}
              </ReactMarkdown>
            )}
          </div>


        </div>

        <div className="flex items-center justify-between mt-2">
          <div className="text-xs text-gray-500 dark:text-gray-400">
            {formatTimestamp(message.timestamp)}
          </div>
          <button
            onClick={() => handleCopy(message.content)}
            className="text-xs text-gray-400 dark:text-gray-500 hover:text-gray-600 dark:hover:text-gray-300 flex items-center space-x-1 transition-colors"
          >
            <Copy className="w-3 h-3" />
            <span>{copied ? 'Copied!' : 'Copy'}</span>
          </button>
        </div>
      </div>
    </motion.div>
  );
};

export default MessageBubble;
