import React, { useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  Plus, 
  Trash2, 
  Upload, 
  Settings, 
  Menu,
  X,
  FileText,
  Clock,
  ExternalLink
} from 'lucide-react';
import { format } from 'date-fns';
import useChatStore from '../../stores/chatStore';
import useRouteStore from '../../stores/routeStore';
import ComvivaLogo from '../ComvivaLogo';
import ThemeToggle from '../ThemeToggle';

const Sidebar = () => {
  const {
    sidebarOpen,
    conversations,
    currentSessionId,
    isLoadingConversations,
    toggleSidebar,
    toggleUploadModal,
    loadConversations,
    startNewConversation,
    deleteConversation,
    setCurrentSession,
  } = useChatStore();

  const { setRoute } = useRouteStore();



  useEffect(() => {
    loadConversations();
  }, [loadConversations]);

  const handleConversationClick = async (sessionId) => {
    if (sessionId !== currentSessionId) {
      await setCurrentSession(sessionId);
    }
  };

  const handleDeleteConversation = async (e, sessionId) => {
    e.stopPropagation();
    if (window.confirm('Are you sure you want to delete this conversation?')) {
      await deleteConversation(sessionId);
    }
  };

  const formatDate = (dateString) => {
    try {
      return format(new Date(dateString), 'MMM d, h:mm a');
    } catch {
      return 'Unknown';
    }
  };

  const sidebarVariants = {
    open: {
      x: 0,
      transition: { type: 'spring', stiffness: 300, damping: 30 }
    },
    closed: {
      x: '-100%',
      transition: { type: 'spring', stiffness: 300, damping: 30 }
    }
  };

  return (
    <>
      {/* Mobile backdrop */}
      <AnimatePresence>
        {sidebarOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={toggleSidebar}
            className="lg:hidden fixed inset-0 bg-gray-900/50 z-40"
          />
        )}
      </AnimatePresence>

      {/* Sidebar */}
      <motion.div
        variants={sidebarVariants}
        animate={sidebarOpen ? 'open' : 'closed'}
        className="fixed lg:relative top-0 left-0 h-full w-80 bg-white dark:bg-dark-surface border-r border-gray-200 dark:border-dark-border z-50 flex flex-col"
      >
        {/* Header */}
        <div className="p-6 border-b border-gray-200 dark:border-dark-border">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-3">
              <ComvivaLogo variant="orb" className="w-8 h-8" />
              <div>
                <h2 className="text-lg font-semibold text-gray-900 dark:text-dark-text">Comviva AI</h2>
                <p className="text-xs text-gray-500 dark:text-dark-muted">A Tech Mahindra Company</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <ThemeToggle />
              <button
                onClick={toggleSidebar}
                className="lg:hidden p-1 text-gray-500 hover:text-gray-700 dark:text-dark-muted dark:hover:text-dark-text"
              >
                <X className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* New Chat Button */}
          <button
            onClick={() => { startNewConversation(); setRoute('chat'); }}
            className="w-full flex items-center justify-center space-x-2 bg-comviva-primary hover:bg-blue-700 text-white py-3 px-4 rounded-lg transition-colors"
          >
            <Plus className="w-4 h-4" />
            <span>New Chat</span>
          </button>
        </div>

        {/* Action Buttons */}
        <div className="p-4 border-b border-gray-200">
          <div className="grid grid-cols-2 gap-2 mb-2">
            <button
              onClick={toggleUploadModal}
              className="flex items-center justify-center space-x-2 bg-gray-100 hover:bg-gray-200 text-gray-700 py-2 px-3 rounded-lg transition-colors text-sm font-semibold"
            >
              <Upload className="w-4 h-4" />
              <span>Upload</span>
            </button>
            <button
              onClick={() => setRoute('jira')}
              className="flex items-center justify-center space-x-2 bg-blue-100 hover:bg-blue-200 text-blue-700 py-2 px-3 rounded-lg transition-colors text-sm"
            >
              <ExternalLink className="w-4 h-4" />
              <span>JIRA</span>
            </button>
          </div>
          <button
            className="w-full flex items-center justify-center space-x-2 bg-gray-100 hover:bg-gray-200 text-gray-700 py-2 px-3 rounded-lg transition-colors text-sm"
            disabled
          >
            <Settings className="w-4 h-4" />
            <span>Settings</span>
          </button>
        </div>

        {/* Conversations List */}
        <div className="flex-1 overflow-hidden">
          <div className="p-4">
            <h3 className="text-sm font-medium text-gray-500 mb-3 flex items-center space-x-2">
              <Clock className="w-4 h-4" />
              <span>Recent Conversations</span>
            </h3>
          </div>

          <div className="flex-1 overflow-y-auto px-4 pb-3 scrollbar-thin" style={{maxHeight: 'calc(100vh - 350px)'}}>
            {isLoadingConversations ? (
              <div className="space-y-3">
                {[...Array(5)].map((_, i) => (
                  <div key={i} className="loading-shimmer h-16 rounded-lg"></div>
                ))}
              </div>
            ) : conversations.length === 0 ? (
              <div className="text-center py-12">
                <MessageSquare className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                <p className="text-sm text-gray-500">No conversations yet</p>
                <p className="text-xs text-gray-400 mt-1">Start a new chat to begin</p>
              </div>
            ) : (
              <div className="space-y-2">
                {conversations.map((conversation) => (
                  <motion.div
                    key={conversation.session_id}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={() => handleConversationClick(conversation.session_id)}
                    className={`
                      p-3 rounded-lg cursor-pointer transition-colors group relative
                      ${currentSessionId === conversation.session_id
                        ? 'bg-comviva-primary/10 border border-comviva-primary/20'
                        : 'hover:bg-gray-50 dark:hover:bg-gray-700 border border-transparent'
                      }
                    `}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1 min-w-0">
                        <h4 className={`
                          text-sm font-medium truncate
                          ${currentSessionId === conversation.session_id
                            ? 'text-comviva-primary'
                            : 'text-gray-900 dark:text-gray-100'
                          }
                        `}>
                          {conversation.title || 'Untitled Chat'}
                        </h4>
                        <div className="flex items-center space-x-2 mt-1">
                          <span className="text-xs text-gray-500 dark:text-gray-400">
                            {conversation.message_count || 0} messages
                          </span>
                          <span className="text-gray-300 dark:text-gray-600">•</span>
                          <span className="text-xs text-gray-500 dark:text-gray-400">
                            {formatDate(conversation.updated_at || conversation.created_at)}
                          </span>
                        </div>
                      </div>

                      <button
                        onClick={(e) => handleDeleteConversation(e, conversation.session_id)}
                        className="opacity-0 group-hover:opacity-100 p-1 text-gray-400 hover:text-red-500 transition-all ml-2"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>

                    {/* Conversation preview */}
                    {conversation.last_message && (
                      <p className="text-xs text-gray-500 dark:text-gray-400 mt-2 line-clamp-2">
                        {conversation.last_message}
                      </p>
                    )}
                  </motion.div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="p-4 border-t border-gray-200 dark:border-dark-border">
          <div className="flex items-center justify-between text-xs text-gray-500 dark:text-dark-muted mb-2">
            <div className="flex items-center space-x-1">
              <FileText className="w-3 h-3" />
              <span>{conversations.length} conversations</span>
            </div>
            <div className="text-comviva-primary font-medium">
              v1.0
            </div>
          </div>
          <div className="flex items-center justify-center">
            <ComvivaLogo variant="full" className="h-4 w-auto opacity-70 hover:opacity-100 transition-opacity" />
          </div>
        </div>
      </motion.div>

      {/* Mobile toggle button */}
      <button
        onClick={toggleSidebar}
        className="lg:hidden fixed top-4 left-4 z-50 p-2 bg-white border border-gray-200 rounded-lg shadow-lg hover:bg-gray-50"
      >
        <Menu className="w-5 h-5" />
      </button>


    </>
  );
};

export default Sidebar;
