import React from 'react';
import { AlertTriangle } from 'lucide-react';

const Disclaimer = () => {
  return (
    <div className="max-w-4xl mx-auto px-6 py-3 border-t border-gray-200 dark:border-dark-border bg-gray-50 dark:bg-dark-bg">
      <div className="flex items-start space-x-2 text-xs text-gray-500 dark:text-gray-400">
        <AlertTriangle className="w-3 h-3 mt-0.5 flex-shrink-0" />
        <p>
          <strong>Disclaimer:</strong> This AI assistant provides information based on available documentation and training data. 
          For critical decisions or latest updates, please consult official Comviva documentation or contact support. 
          Responses may not always be complete or up-to-date.
        </p>
      </div>
    </div>
  );
};

export default Disclaimer;
