import { create } from 'zustand';
import { persist } from 'zustand/middleware';

const useSettingsStore = create(
  persist(
    (set, get) => ({
      // UI Settings
      zoomLevel: 1.0,

      // Actions
      setZoomLevel: (level) => {
        const clampedLevel = Math.max(0.8, Math.min(1.3, level));
        set({ zoomLevel: clampedLevel });

        // Apply zoom to document root
        document.documentElement.style.fontSize = `${clampedLevel}rem`;
        document.documentElement.style.transform = `scale(${clampedLevel})`;
        document.documentElement.style.transformOrigin = 'top left';

        // Adjust body width to compensate for scaling
        if (clampedLevel !== 1.0) {
          document.body.style.width = `${100 / clampedLevel}%`;
          document.body.style.height = `${100 / clampedLevel}%`;
        } else {
          document.body.style.width = '';
          document.body.style.height = '';
        }
      },

      resetZoom: () => {
        get().setZoomLevel(1.0);
      },

      // Initialize settings on load
      initializeSettings: () => {
        const { zoomLevel } = get();
        get().setZoomLevel(zoomLevel);
      }
    }),
    {
      name: 'comviva-settings-store',
      partialize: (state) => ({ zoomLevel: state.zoomLevel }),
    }
  )
);

export default useSettingsStore;
